sys_script_include.script:
var Predictive_Intelligence_LLM_client = Class.create();
Predictive_Intelligence_LLM_client.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    type: 'Predictive_Intelligence_LLM_client',

	generateSolution: function() {
		var description = this.getParameter('sysparm_description');
		var getSolution = new x_snc_llm.Predictive_Intelligence_LLM();
		var response = getSolution.workflowGenerateSolution(description);
		return response;

	},
});
